import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { UserService } from 'src/app/services/user.service';
import { ActivatedRoute,Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {
   loginForm!:FormGroup;
   isSubmitted=false;
   returnUrl='';
  constructor(private fb:FormBuilder, private userService:UserService,
    private ActivatedRoute:ActivatedRoute,
    private router:Router){}

  ngOnInit(): void {
     this.loginForm=this.fb.group({
      email:['',  Validators.required,Validators.email],
      password:['',Validators.required]
     }) 
     this.returnUrl = this.ActivatedRoute.snapshot.queryParams.returnUrl;
  }
  get fc(){
    return  this.loginForm.controls;
  }
  submit(){
    this.isSubmitted=true;
    if(this.loginForm.invalid) return;

    this.userService.login({email:this.fc.email.value,password:this.fc.password.
      value}).subscribe(()=>{
        this.router.navigateByUrl(this.returnUrl);
        this.router.navigate(["../home"]);
      })
  }
    }
<section class="frontpage">
  <img src="../../../assets/Frontimg/Homeimg.png">
        <h4><b>Trade-In-Offer</b></h4>
        <h2>Super value deals </h2>
        <h1>On all products</h1>
        <h5>Save more with coupons upto 50% off !...</h5>
        <mark>Login to Shop now</mark>

    </section>


<div class="container">
     <div class="details">

        <app-title title="Login"></app-title>
        <form [formGroup]="loginForm" (ngSubmit)="submit()">
            <div class="input-container">
                <div class="input-container">
                    <label for="email">Email</label>
                    <div>
                        <input type="email" placeholder="abd@gmail.com" formControlName="email">
                    </div>
                    <div class="error-list" *ngIf="fc.email.errors && isSubmitted">
                        <div *ngIf="fc.email.errors.required">Should not be empty</div>
                        <div *ngIf="fc.email.errors.email">MailId is not correct</div>
                    </div>
                </div>
                <div class="input-container">
                    <label for="password">Password</label>
                </div>

                <div>
                    <input type="password" placeholder="*******" formControlName="password">
                </div>
                 <div class="error-list" *ngIf="fc.password.errors && isSubmitted">
                        <div *ngIf="fc.password.errors.required">Should not be empty</div>
                    </div>
                <div class="button">
                    <button type="submit">Login</button>
                </div>
            </div>
        </form>
    </div>

</div>
