import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {
   loginForm!:FormGroup;
   isSubmitted=false;
  constructor(private fb:FormBuilder){}

  ngOnInit(): void {
     this.loginForm=this.fb.group({
      email:['',  Validators.required,Validators.email],
      password:['',Validators.required]
     }) 
  }
  get fc(){
    return  this.loginForm.controls;
  }
  submit(){
    this.isSubmitted=true;
    if(this.loginForm.invalid) return;

    alert(`Email: ${this.fc.email.value},
    Password:${this.fc.password.value}`)
  }
    }
<div class="container">
    <section class="background-image">
        <img src="../../../../assets/Frontimg/shoppinh" alt="">
     <div class="details">

        <app-title title="Login"></app-title>
        <form [formGroup]="loginForm" (ngSubmit)="submit()">
            <div class="input-container">
                <div class="input-container">
                    <label for="email">Email</label>
                    <div>
                        <input type="email" placeholder="abd@gmail.com" formControlName="email">
                    </div>
                    <div class="error-list" *ngIf="fc.email.errors && isSubmitted">
                        <div *ngIf="fc.email.errors.required">Should not be empty</div>
                        <div *ngIf="fc.email.errors.email">MailId is not correct</div>
                    </div>
                </div>
                <div class="input-container">
                    <label for="password">Password</label>
                </div>

                <div>
                    <input type="password" placeholder="*******" formControlName="password">
                </div>
                 <div class="error-list" *ngIf="fc.password.errors && isSubmitted">
                        <div *ngIf="fc.password.errors.required">Should not be empty</div>
                    </div>
                <div class="button">
                    <button type="submit">Login</button>
                </div>
            </div>
        </form>
    </div>
    </section>

</div>
